mapper.insert:
insert into blog ( id,title, body, author_id, status, created_at, saved_at
, published_title, published_body, published_at
) values ( #{blog.id}, #{blog.title}, #{blog.body}, #{blog.authorId}, #{blog.status}, #{blog.createdAt}, #{blog.savedAt}
, #{blog.published.publishedTitle}, #{blog.published.publishedBody}, #{blog.published.publishedAt}
)

mapper.update:
update blog set id = #{blog.id}, title = #{blog.title}, body = #{blog.body}, author_id = #{blog.authorId}, status = #{blog.status}, created_at = #{blog.createdAt}, saved_at = #{blog.savedAt}
, published_title = #{blog.published.publishedTitle}, published_body = #{blog.published.publishedBody}, published_at = #{blog.published.publishedAt}
where id = #{blog.id}

mapper.select:
select count(id)
from blog
where id = #{id}
